"use client"

import * as React from "react"
import Link from "next/link"

import { useGithubMention } from "@/hooks/use-github-mention"
import { parseGithubUrl } from "@/lib/github/parse-url"
import type { GithubResource } from "@/lib/github/types"
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar"
import { Badge } from "@/components/ui/badge"
import { Button } from "@/components/ui/button"
import { Card, CardContent } from "@/components/ui/card"
import { Separator } from "@/components/ui/separator"
import { Skeleton } from "@/components/ui/skeleton"
import { Tooltip, TooltipContent, TooltipTrigger } from "@/components/ui/tooltip"

type GithubMentionProps = {
  url: string
  useServer?: boolean
  className?: string
}

export function GithubMention({ url, useServer, className }: GithubMentionProps) {
  const { data, isLoading, error, refetch } = useGithubMention(url, { useServer })

  if (isLoading) return <GithubMentionSkeleton className={className} />
  if (error || !data) return <GithubMentionError url={url} onRetry={refetch} className={className} />

  return (
    <Card className={className}>
      <CardContent className="flex items-start gap-4">
        {renderContent(data)}
      </CardContent>
    </Card>
  )
}

function GithubMentionSkeleton({ className }: { className?: string }) {
  return (
    <Card className={className}>
      <CardContent className="flex items-start gap-4">
        <Skeleton className="size-8 rounded-full" />
        <div className="flex-1 space-y-2">
          <Skeleton className="h-4 w-1/2" />
          <Skeleton className="h-3 w-1/3" />
          <Skeleton className="h-3 w-2/3" />
        </div>
      </CardContent>
    </Card>
  )
}

function GithubMentionError({ url, onRetry, className }: { url: string; onRetry: () => void; className?: string }) {
  const parsed = parseGithubUrl(url)
  return (
    <Card aria-live="polite" className={className}>
      <CardContent className="flex items-center justify-between gap-4">
        <div className="min-w-0">
          <p className="text-sm font-medium">Could not load GitHub resource</p>
          <p className="text-muted-foreground text-xs truncate">{parsed.kind !== "unknown" ? url : "Invalid GitHub URL"}</p>
        </div>
        <Button size="sm" onClick={onRetry} aria-label="Retry loading GitHub data">
          Retry
        </Button>
      </CardContent>
    </Card>
  )
}

function renderContent(data: GithubResource) {
  switch (data.kind) {
    case "pull":
      return <PullContent data={data} />
    case "issue":
      return <IssueContent data={data} />
    case "user":
      return <UserContent data={data} />
    case "repo":
      return <RepoContent data={data} />
    default:
      return <p className="text-sm">Unsupported GitHub URL</p>
  }
}

function PullContent({ data }: { data: Extract<GithubResource, { kind: "pull" }> }) {
  const status = data.merged ? "merged" : data.draft ? "draft" : data.state
  const statusVariant = data.merged ? "secondary" : data.state === "open" ? "default" : "outline"
  const created = new Date(data.created_at).toLocaleDateString(undefined, { year: "numeric", month: "short", day: "numeric" })
  const labels = (data.labels ?? []).slice(0, 3)
  const extra = (data.labels?.length ?? 0) - labels.length

  return (
    <div className="flex w-full items-start gap-3">
      <Avatar>
        <AvatarImage src={data.user.avatar_url ?? undefined} alt={`${data.user.login} avatar`} />
        <AvatarFallback aria-hidden>{data.user.login?.slice(0, 2).toUpperCase()}</AvatarFallback>
      </Avatar>
      <div className="min-w-0 flex-1">
        <div className="flex items-center gap-2">
          <Badge variant={statusVariant as any} aria-label={`PR status: ${status}`}>{statusLabel(status)}</Badge>
          <Link href={data.html_url} className="hover:underline focus:outline-none focus-visible:ring-2 focus-visible:ring-ring rounded-sm text-sm font-medium">
            #{data.number} {data.title}
          </Link>
        </div>
        <div className="text-muted-foreground mt-1 flex items-center gap-2 text-xs">
          <span>
            by <Link href={data.user.html_url} className="hover:underline">{data.user.login}</Link>
          </span>
          <Separator className="mx-1 h-3 w-px" />
          <span>opened {created}</span>
          {data.base?.repo?.full_name ? (
            <>
              <Separator className="mx-1 h-3 w-px" />
              <span className="truncate">{data.base.repo.full_name}</span>
            </>
          ) : null}
        </div>
        {!!labels.length && (
          <div className="mt-2 flex flex-wrap items-center gap-1">
            {labels.map(l => (
              <Badge key={l.id} variant="secondary" aria-label={`Label ${l.name}`} className="truncate max-w-[10rem]">
                {l.name}
              </Badge>
            ))}
            {extra > 0 && (
              <Tooltip>
                <TooltipTrigger asChild>
                  <Badge variant="outline" aria-label={`+${extra} more labels`}>+{extra}</Badge>
                </TooltipTrigger>
                <TooltipContent>
                  {(data.labels ?? []).slice(labels.length).map(l => l.name).join(", ")}
                </TooltipContent>
              </Tooltip>
            )}
          </div>
        )}
      </div>
    </div>
  )
}

function IssueContent({ data }: { data: Extract<GithubResource, { kind: "issue" }> }) {
  const status = data.state
  const statusVariant = data.state === "open" ? "default" : "outline"
  const created = new Date(data.created_at).toLocaleDateString(undefined, { year: "numeric", month: "short", day: "numeric" })

  return (
    <div className="flex w-full items-start gap-3">
      <Avatar>
        <AvatarImage src={data.user.avatar_url ?? undefined} alt={`${data.user.login} avatar`} />
        <AvatarFallback aria-hidden>{data.user.login?.slice(0, 2).toUpperCase()}</AvatarFallback>
      </Avatar>
      <div className="min-w-0 flex-1">
        <div className="flex items-center gap-2">
          <Badge variant={statusVariant as any} aria-label={`Issue status: ${status}`}>{statusLabel(status)}</Badge>
          <Link href={data.html_url} className="hover:underline focus:outline-none focus-visible:ring-2 focus-visible:ring-ring rounded-sm text-sm font-medium">
            #{data.number} {data.title}
          </Link>
        </div>
        <div className="text-muted-foreground mt-1 flex items-center gap-2 text-xs">
          <span>
            by <Link href={data.user.html_url} className="hover:underline">{data.user.login}</Link>
          </span>
          <Separator className="mx-1 h-3 w-px" />
          <span>opened {created}</span>
          {typeof data.comments === "number" && (
            <>
              <Separator className="mx-1 h-3 w-px" />
              <span>{data.comments} comments</span>
            </>
          )}
        </div>
      </div>
    </div>
  )
}

function UserContent({ data }: { data: Extract<GithubResource, { kind: "user" }> }) {
  return (
    <div className="flex w-full items-start gap-3">
      <Avatar className="size-10">
        <AvatarImage src={data.avatar_url ?? undefined} alt={`${data.login} avatar`} />
        <AvatarFallback aria-hidden>{data.login?.slice(0, 2).toUpperCase()}</AvatarFallback>
      </Avatar>
      <div className="min-w-0 flex-1">
        <Link href={data.html_url} className="hover:underline focus:outline-none focus-visible:ring-2 focus-visible:ring-ring rounded-sm text-sm font-medium">
          {data.name ?? data.login} <span className="text-muted-foreground">@{data.login}</span>
        </Link>
        {data.bio ? <p className="text-muted-foreground mt-1 line-clamp-2 text-xs">{data.bio}</p> : null}
        <div className="text-muted-foreground mt-2 flex flex-wrap items-center gap-3 text-xs">
          {typeof data.followers === "number" && <span>{data.followers} followers</span>}
          {typeof data.following === "number" && <span>{data.following} following</span>}
          {data.location ? <span className="truncate">{data.location}</span> : null}
        </div>
      </div>
    </div>
  )
}

function RepoContent({ data }: { data: Extract<GithubResource, { kind: "repo" }> }) {
  return (
    <div className="flex w-full items-start gap-3">
      <div className="size-8 shrink-0 rounded-full border text-xs font-medium grid place-items-center" aria-hidden>
        {data.name[0]?.toUpperCase()}
      </div>
      <div className="min-w-0 flex-1">
        <Link href={data.html_url} className="hover:underline focus:outline-none focus-visible:ring-2 focus-visible:ring-ring rounded-sm text-sm font-medium">
          {data.full_name}
        </Link>
        {data.description ? <p className="text-muted-foreground mt-1 line-clamp-2 text-xs">{data.description}</p> : null}
        <div className="text-muted-foreground mt-2 flex flex-wrap items-center gap-3 text-xs">
          {typeof data.stargazers_count === "number" && <span>★ {data.stargazers_count}</span>}
          {typeof data.forks_count === "number" && <span>⑂ {data.forks_count}</span>}
          {typeof data.open_issues_count === "number" && <span>issues {data.open_issues_count}</span>}
        </div>
      </div>
    </div>
  )
}

function statusLabel(status: string) {
  switch (status) {
    case "open":
      return "Open"
    case "closed":
      return "Closed"
    case "merged":
      return "Merged"
    case "draft":
      return "Draft"
    default:
      return status
  }
}

export default GithubMention
